import type { NetworkConfig } from '../network-integration';

/**
 * Network Configuration Management
 * Handles different network equipment configurations
 */

export const DEFAULT_NETWORK_CONFIG: NetworkConfig = {
  // Router/Gateway Configuration
  routerType: 'mikrotik',
  routerHost: process.env.ROUTER_HOST || '192.168.1.1',
  routerPort: parseInt(process.env.ROUTER_PORT || '8728'),
  routerUsername: process.env.ROUTER_USERNAME || 'admin',
  routerPassword: process.env.ROUTER_PASSWORD || '',

  // RADIUS Server Configuration
  radiusHost: process.env.RADIUS_HOST || '127.0.0.1',
  radiusPort: parseInt(process.env.RADIUS_PORT || '1812'),
  radiusSecret: process.env.RADIUS_SECRET || 'testing123',

  // Network Settings
  hotspotInterface: process.env.HOTSPOT_INTERFACE || 'wlan1',
  clientNetwork: process.env.CLIENT_NETWORK || '192.168.100.0/24',
  dnsServers: process.env.DNS_SERVERS?.split(',') || ['8.8.8.8', '8.8.4.4'],

  // Captive Portal Settings
  portalUrl: process.env.PORTAL_URL || 'http://192.168.1.1:3000',
  redirectUrl: process.env.REDIRECT_URL || 'http://google.com',
};

/**
 * Equipment-specific configurations
 */
export const EQUIPMENT_PROFILES = {
  mikrotik_hap: {
    routerType: 'mikrotik' as const,
    routerPort: 8728,
    hotspotInterface: 'wlan1',
    clientNetwork: '192.168.100.0/24',
  },
  mikrotik_routerboard: {
    routerType: 'mikrotik' as const,
    routerPort: 8728,
    hotspotInterface: 'bridge-hotspot',
    clientNetwork: '192.168.100.0/24',
  },
  pfsense_standard: {
    routerType: 'pfsense' as const,
    routerPort: 443,
    hotspotInterface: 'em1',
    clientNetwork: '192.168.200.0/24',
  },
  unifi_controller: {
    routerType: 'generic' as const,
    routerPort: 8443,
    hotspotInterface: 'eth0',
    clientNetwork: '192.168.10.0/24',
  },
};

export function getNetworkConfig(equipmentType?: string): NetworkConfig {
  const baseConfig = { ...DEFAULT_NETWORK_CONFIG };

  if (equipmentType && equipmentType in EQUIPMENT_PROFILES) {
    const profile = EQUIPMENT_PROFILES[equipmentType as keyof typeof EQUIPMENT_PROFILES];
    return { ...baseConfig, ...profile };
  }

  return baseConfig;
}

client/src/components/captive-portal.tsx.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Checkbox } from "@/components/ui/checkbox";
import { Form, FormControl, FormField, FormItem } from "@/components/ui/form";
import { Wifi } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

const redeemVoucherSchema = z.object({
  code: z.string().min(1, "Please enter a voucher code"),
  acceptTerms: z.boolean().refine((val) => val === true, "You must accept the terms and conditions"),
});

type RedeemVoucherForm = z.infer<typeof redeemVoucherSchema>;

interface CaptivePortalProps {
  businessName?: string;
  welcomeMessage?: string;
  primaryColor?: string;
  termsRequired?: boolean;
}

export default function CaptivePortal({
  businessName = "Coffee & Co.",
  welcomeMessage = "Welcome to our free WiFi! Enter your voucher code to get connected.",
  primaryColor = "#3B82F6",
  termsRequired = true,
}: CaptivePortalProps) {
  const { toast } = useToast();
  const [isConnecting, setIsConnecting] = useState(false);

  const form = useForm<RedeemVoucherForm>({
    resolver: zodResolver(redeemVoucherSchema),
    defaultValues: {
      acceptTerms: false,
    },
  });

  const redeemVoucherMutation = useMutation({
    mutationFn: async (data: { code: string }) => {
      const deviceInfo = {
        ipAddress: "192.168.1.100", // This would be detected by the actual system
        macAddress: "00:11:22:33:44:55",
        deviceType: "laptop",
        userAgent: navigator.userAgent,
      };

      const response = await apiRequest("POST", "/api/vouchers/redeem", {
        code: data.code,
        deviceInfo,
      });
      return response.json();
    },
    onSuccess: (data) => {
      setIsConnecting(true);
      setTimeout(() => {
        toast({
          title: "Connected!",
          description: "You are now connected to the WiFi network.",
        });
        setIsConnecting(false);
      }, 2000);
    },
    onError: (error) => {
      toast({
        title: "Connection Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: RedeemVoucherForm) => {
    redeemVoucherMutation.mutate({ code: data.code });
  };

  if (isConnecting) {
    return (
      <div className="max-w-sm mx-auto">
        <Card className="border border-gray-200 rounded-lg overflow-hidden">
          <div 
            className="h-32 bg-cover bg-center"
            style={{
              backgroundImage: "url('https://images.unsplash.com/photo-1554118811-1e0d58224f24?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=800&h=300')"
            }}
          />
          <CardContent className="p-6 text-center">
            <div className="w-16 h-16 bg-primary bg-opacity-10 rounded-full flex items-center justify-center mx-auto mb-4">
              <Wifi className="text-primary animate-pulse" size={32} />
            </div>
            <h3 className="text-xl font-bold text-gray-900 mb-2">Connecting...</h3>
            <p className="text-sm text-gray-600 mb-6">Please wait while we connect you to the WiFi network.</p>
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div className="bg-primary h-2 rounded-full animate-pulse" style={{ width: '75%' }}></div>
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="max-w-sm mx-auto">
      <Card className="border border-gray-200 rounded-lg overflow-hidden">
        <div 
          className="h-32 bg-cover bg-center"
          style={{
            backgroundImage: "url('https://images.unsplash.com/photo-1554118811-1e0d58224f24?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=800&h=300')"
          }}
        />
        <CardContent className="p-6 text-center">
          <div className="w-16 h-16 bg-primary bg-opacity-10 rounded-full flex items-center justify-center mx-auto mb-4">
            <Wifi className="text-primary" size={32} />
          </div>
          <h3 className="text-xl font-bold text-gray-900 mb-2">{businessName}</h3>
          <p className="text-sm text-gray-600 mb-6">{welcomeMessage}</p>

          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="code"
                render={({ field }) => (
                  <FormItem>
                    <FormControl>
                      <Input
                        placeholder="Enter voucher code"
                        className="text-center font-mono text-lg"
                        {...field}
                      />
                    </FormControl>
                  </FormItem>
                )}
              />

              <Button 
                type="submit" 
                className="w-full"
                disabled={redeemVoucherMutation.isPending}
                style={{ backgroundColor: primaryColor }}
              >
                {redeemVoucherMutation.isPending ? "Connecting..." : "Connect to WiFi"}
              </Button>

              {termsRequired && (
                <div className="pt-4 border-t border-gray-200">
                  <FormField
                    control={form.control}
                    name="acceptTerms"
                    render={({ field }) => (
                      <FormItem className="flex items-center space-x-2">
                        <FormControl>
                          <Checkbox
                            checked={field.value}
                            onCheckedChange={field.onChange}
                          />
                        </FormControl>
                        <span className="text-xs text-gray-500">
                          I agree to the Terms & Conditions
                        </span>
                      </FormItem>
                    )}
                  />
                </div>
              )}
            </form>
          </Form>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

