import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { Label } from "@/components/ui/label";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Save, Smartphone } from "lucide-react";
import CaptivePortal from "@/components/captive-portal";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

const portalSettingsSchema = z.object({
  businessName: z.string().min(1, "Business name is required"),
  welcomeMessage: z.string().min(1, "Welcome message is required"),
  primaryColor: z.string().min(7, "Please select a color"),
  termsRequired: z.boolean(),
  termsContent: z.string().optional(),
});

type PortalSettingsForm = z.infer<typeof portalSettingsSchema>;

export default function Portal() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  useEffect(() => {
    const event = new CustomEvent('pageChange', {
      detail: {
        title: 'Captive Portal',
        subtitle: 'Customize your branded WiFi login experience'
      }
    });
    window.dispatchEvent(event);
  }, []);

  const { data: portalSettings, isLoading } = useQuery({
    queryKey: ["/api/portal/settings"],
  });

  const form = useForm<PortalSettingsForm>({
    resolver: zodResolver(portalSettingsSchema),
    defaultValues: {
      businessName: "Coffee & Co.",
      welcomeMessage: "Welcome to our free WiFi! Enter your voucher code to get connected.",
      primaryColor: "#3B82F6",
      termsRequired: true,
      termsContent: "",
    },
  });

  // Update form when data loads
  useEffect(() => {
    if (portalSettings) {
      form.reset({
        businessName: portalSettings.businessName || "Coffee & Co.",
        welcomeMessage: portalSettings.welcomeMessage || "Welcome to our free WiFi! Enter your voucher code to get connected.",
        primaryColor: portalSettings.primaryColor || "#3B82F6",
        termsRequired: portalSettings.termsRequired ?? true,
        termsContent: portalSettings.termsContent || "",
      });
    }
  }, [portalSettings, form]);

  const updateSettingsMutation = useMutation({
    mutationFn: async (data: PortalSettingsForm) => {
      await apiRequest("PUT", "/api/portal/settings", data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Portal settings updated successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/portal/settings"] });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: PortalSettingsForm) => {
    updateSettingsMutation.mutate(data);
  };

  // Watch form values for live preview
  const watchedValues = form.watch();

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {[...Array(2)].map((_, i) => (
            <Card key={i} className="animate-pulse">
              <CardContent className="p-6">
                <div className="h-96 bg-gray-200 rounded"></div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-lg font-semibold text-gray-900">Captive Portal</h3>
          <p className="text-sm text-gray-500">Customize your branded WiFi login experience</p>
        </div>
        <Button 
          onClick={form.handleSubmit(onSubmit)}
          disabled={updateSettingsMutation.isPending}
          className="flex items-center space-x-2"
        >
          <Save size={16} />
          <span>{updateSettingsMutation.isPending ? "Saving..." : "Save Changes"}</span>
        </Button>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Portal Settings */}
        <Card>
          <CardHeader>
            <CardTitle>Portal Settings</CardTitle>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="businessName"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Business Name</FormLabel>
                      <FormControl>
                        <Input {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="welcomeMessage"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Welcome Message</FormLabel>
                      <FormControl>
                        <Textarea rows={3} {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="primaryColor"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Primary Color</FormLabel>
                      <div className="flex items-center space-x-3">
                        <FormControl>
                          <input
                            type="color"
                            {...field}
                            className="w-12 h-10 border border-gray-300 rounded cursor-pointer"
                          />
                        </FormControl>
                        <FormControl>
                          <Input {...field} className="flex-1" />
                        </FormControl>
                      </div>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center">
                  <div className="text-gray-400 text-2xl mb-2">â˜ï¸</div>
                  <p className="text-sm text-gray-500 mb-1">Click to upload or drag and drop</p>
                  <p className="text-xs text-gray-400">PNG, JPG up to 2MB</p>
                </div>

                <FormField
                  control={form.control}
                  name="termsRequired"
                  render={({ field }) => (
                    <FormItem className="flex items-center justify-between">
                      <FormLabel>Terms & Conditions Required</FormLabel>
                      <FormControl>
                        <Switch
                          checked={field.value}
                          onCheckedChange={field.onChange}
                        />
                      </FormControl>
                    </FormItem>
                  )}
                />

                {watchedValues.termsRequired && (
                  <FormField
                    control={form.control}
                    name="termsContent"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Terms & Conditions Content</FormLabel>
                        <FormControl>
                          <Textarea 
                            rows={4} 
                            placeholder="Enter your terms and conditions..."
                            {...field} 
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                )}
              </form>
            </Form>
          </CardContent>
        </Card>

        {/* Portal Preview */}
        <Card>
          <CardHeader className="flex flex-row items-center justify-between">
            <CardTitle>Live Preview</CardTitle>
            <div className="flex items-center space-x-2 text-sm text-gray-500">
              <Smartphone size={16} />
              <span>Mobile View</span>
            </div>
          </CardHeader>
          <CardContent>
            <div className="flex justify-center">
              <CaptivePortal
                businessName={watchedValues.businessName}
                welcomeMessage={watchedValues.welcomeMessage}
                primaryColor={watchedValues.primaryColor}
                termsRequired={watchedValues.termsRequired}
              />
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/sessions.tsx.content:
import { useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { 
  RefreshCw, 
  Activity, 
  Gauge, 
  TrendingUp, 
  Clock,
  Smartphone,
  Laptop,
  Tablet,
  Monitor
} from "lucide-react";
import { formatBytes, getDeviceIcon } from "@/lib/utils";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

export default function Sessions() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  useEffect(() => {
    const event = new CustomEvent('pageChange', {
      detail: {
        title: 'Active Sessions',
        subtitle: 'Monitor connected users and their network usage'
      }
    });
    window.dispatchEvent(event);
  }, []);

  const { data: sessions = [], isLoading, refetch } = useQuery({
    queryKey: ["/api/sessions/active"],
    refetchInterval: 10000, // Refresh every 10 seconds
  });

  const { data: sessionStats = {} } = useQuery({
    queryKey: ["/api/dashboard/stats"],
    refetchInterval: 30000,
  });

  const disconnectMutation = useMutation({
    mutationFn: async (sessionId: number) => {
      await apiRequest("POST", `/api/sessions/${sessionId}/disconnect`);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "User disconnected successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/sessions/active"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const getDeviceIconComponent = (deviceType: string) => {
    switch (deviceType?.toLowerCase()) {
      case 'mobile':
      case 'phone':
        return <Smartphone size={16} className="text-primary" />;
      case 'laptop':
        return <Laptop size={16} className="text-secondary" />;
      case 'tablet':
        return <Tablet size={16} className="text-purple-600" />;
      case 'desktop':
        return <Monitor size={16} className="text-gray-600" />;
      default:
        return <Monitor size={16} className="text-gray-600" />;
    }
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          {[...Array(4)].map((_, i) => (
            <Card key={i} className="animate-pulse">
              <CardContent className="p-4">
                <div className="h-16 bg-gray-200 rounded"></div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-lg font-semibold text-gray-900">Active Sessions</h3>
          <p className="text-sm text-gray-500">Monitor connected users and their network usage</p>
        </div>
        <Button onClick={() => refetch()} className="flex items-center space-x-2">
          <RefreshCw size={16} />
          <span>Refresh</span>
        </Button>
      </div>

      {/* Live Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600">Connected Now</p>
                <p className="text-2xl font-bold text-green-600">
                  {sessionStats.connectedUsers || sessions.length}
                </p>
              </div>
              <div className="flex items-center">
                <div className="w-3 h-3 bg-green-500 rounded-full animate-pulse mr-2"></div>
                <Activity className="text-green-600" size={20} />
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600">Bandwidth Used</p>
                <p className="text-2xl font-bold text-yellow-600">2.4 Mbps</p>
              </div>
              <Gauge className="text-yellow-600" size={32} />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600">Peak Today</p>
                <p className="text-2xl font-bold text-primary">
                  {sessionStats.peakToday || 156}
                </p>
              </div>
              <TrendingUp className="text-primary" size={32} />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-gray-600">Avg. Duration</p>
                <p className="text-2xl font-bold text-gray-700">
                  {sessionStats.avgDuration || '1.2h'}
                </p>
              </div>
              <Clock className="text-gray-500" size={32} />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Sessions Table */}
      <Card>
        <CardHeader>
          <CardTitle>Current Sessions</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="overflow-x-auto">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>User</TableHead>
                  <TableHead>Voucher</TableHead>
                  <TableHead>Connected</TableHead>
                  <TableHead>Data Used</TableHead>
                  <TableHead>Time Left</TableHead>
                  <TableHead>Speed</TableHead>
                  <TableHead>Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {sessions.length === 0 ? (
                  <TableRow>
                    <TableCell colSpan={7} className="text-center py-8 text-gray-500">
                      No active sessions
                    </TableCell>
                  </TableRow>
                ) : (
                  sessions.map((session: any) => {
                    const dataUsedPercent = session.voucher?.plan?.dataLimit 
                      ? (session.dataUsed / session.voucher.plan.dataLimit) * 100
                      : 0;

                    const timeConnected = Math.floor(
                      (new Date().getTime() - new Date(session.startTime).getTime()) / (1000 * 60)
                    );

                    return (
                      <TableRow key={session.id} className="hover:bg-gray-50">
                        <TableCell>
                          <div className="flex items-center space-x-3">
                            <div className="w-8 h-8 bg-primary bg-opacity-10 rounded-full flex items-center justify-center">
                              {getDeviceIconComponent(session.deviceType)}
                            </div>
                            <div>
                              <div className="text-sm font-medium text-gray-900">
                                {session.ipAddress}
                              </div>
                              <div className="text-sm text-gray-500 capitalize">
                                {session.deviceType || 'Unknown'}
                              </div>
                            </div>
                          </div>
                        </TableCell>
                        <TableCell>
                          <code className="font-mono text-sm text-gray-900">
                            {session.voucher?.code || 'N/A'}
                          </code>
                        </TableCell>
                        <TableCell className="text-sm text-gray-900">
                          {timeConnected}m ago
                        </TableCell>
                        <TableCell>
                          <div className="text-sm text-gray-900">
                            {formatBytes(session.dataUsed * 1024 * 1024)} / {' '}
                            {session.voucher?.plan?.dataLimit 
                              ? formatBytes(session.voucher.plan.dataLimit * 1024 * 1024)
                              : 'Unlimited'
                            }
                          </div>
                          {session.voucher?.plan?.dataLimit && (
                            <Progress value={dataUsedPercent} className="w-full mt-1" />
                          )}
                        </TableCell>
                        <TableCell className="text-sm text-gray-900">
                          {session.voucher?.plan?.duration 
                            ? `${Math.max(0, session.voucher.plan.duration - timeConnected)}m`
                            : 'Unlimited'
                          }
                        </TableCell>
                        <TableCell className="text-sm text-gray-900">
                          <div>â†“ 2.1 Mbps</div>
                          <div>â†‘ 0.8 Mbps</div>
                        </TableCell>
                        <TableCell>
                          <Button
                            variant="ghost"
                            size="sm"
                            className="text-red-600 hover:text-red-900"
                            onClick={() => disconnectMutation.mutate(session.id)}
                            disabled={disconnectMutation.isPending}
                          >
                            Disconnect
                          </Button>
                        </TableCell>
                      </TableRow>
                    );
                  })
                )}
              </TableBody>
            </Table>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/settings.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Switch } from "@/components/ui/switch";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Save, Wifi, Shield, Bell, Network } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

const networkSettingsSchema = z.object({
  ssid: z.string().min(1, "WiFi network name is required"),
  gatewayIp: z.string().regex(/^(?:[0-9]{1,3}\.){3}[0-9]{1,3}$/, "Invalid IP address"),
  dnsServers: z.string().min(1, "DNS servers are required"),
  maxConcurrentUsers: z.number().min(1, "Must be at least 1").max(10000, "Maximum 10,000 users"),
});

const voucherDefaultsSchema = z.object({
  defaultDuration: z.number().min(1, "Duration must be at least 1 minute"),
  defaultDataLimit: z.number().optional(),
  defaultSpeedLimitDown: z.number().min(1, "Download speed must be at least 1 Mbps"),
  defaultSpeedLimitUp: z.number().min(1, "Upload speed must be at least 1 Mbps"),
  maxDevicesPerVoucher: z.number().min(1, "Must allow at least 1 device").max(10, "Maximum 10 devices"),
});

const securitySettingsSchema = z.object({
  contentFiltering: z.boolean(),
  requireTermsAcceptance: z.boolean(),
  sessionLogging: z.boolean(),
  sessionTimeout: z.number().min(1, "Timeout must be at least 1 minute").max(1440, "Maximum 24 hours"),
});

const notificationSettingsSchema = z.object({
  emailNotifications: z.boolean(),
  adminEmail: z.string().email("Invalid email address").optional(),
  lowCreditAlerts: z.boolean(),
  alertThreshold: z.number().min(1, "Threshold must be at least 1"),
});

type NetworkSettingsForm = z.infer<typeof networkSettingsSchema>;
type VoucherDefaultsForm = z.infer<typeof voucherDefaultsSchema>;
type SecuritySettingsForm = z.infer<typeof securitySettingsSchema>;
type NotificationSettingsForm = z.infer<typeof notificationSettingsSchema>;

export default function Settings() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [activeTab, setActiveTab] = useState("network");

  useEffect(() => {
    const event = new CustomEvent('pageChange', {
      detail: {
        title: 'System Settings',
        subtitle: 'Configure your WiFi voucher system preferences'
      }
    });
    window.dispatchEvent(event);
  }, []);

  const { data: settings = [], isLoading } = useQuery({
    queryKey: ["/api/settings"],
  });

  // Convert settings array to object for easier access
  const settingsObj = settings.reduce((acc: any, setting: any) => {
    acc[setting.key] = setting.value;
    return acc;
  }, {});

  const networkForm = useForm<NetworkSettingsForm>({
    resolver: zodResolver(networkSettingsSchema),
    defaultValues: {
      ssid: "Guest-WiFi",
      gatewayIp: "192.168.1.1",
      dnsServers: "8.8.8.8, 8.8.4.4",
      maxConcurrentUsers: 200,
    },
  });

  const voucherForm = useForm<VoucherDefaultsForm>({
    resolver: zodResolver(voucherDefaultsSchema),
    defaultValues: {
      defaultDuration: 120, // 2 hours in minutes
      defaultDataLimit: 1024, // 1GB in MB
      defaultSpeedLimitDown: 10,
      defaultSpeedLimitUp: 5,
      maxDevicesPerVoucher: 3,
    },
  });

  const securityForm = useForm<SecuritySettingsForm>({
    resolver: zodResolver(securitySettingsSchema),
    defaultValues: {
      contentFiltering: true,
      requireTermsAcceptance: true,
      sessionLogging: true,
      sessionTimeout: 30,
    },
  });

  const notificationForm = useForm<NotificationSettingsForm>({
    resolver: zodResolver(notificationSettingsSchema),
    defaultValues: {
      emailNotifications: true,
      adminEmail: "admin@example.com",
      lowCreditAlerts: true,
      alertThreshold: 50,
    },
  });

  // Update forms when settings load
  useEffect(() => {
    if (settings.length > 0) {
      networkForm.reset({
        ssid: settingsObj.ssid || "Guest-WiFi",
        gatewayIp: settingsObj.gatewayIp || "192.168.1.1",
        dnsServers: settingsObj.dnsServers || "8.8.8.8, 8.8.4.4",
        maxConcurrentUsers: parseInt(settingsObj.maxConcurrentUsers) || 200,
      });

      voucherForm.reset({
        defaultDuration: parseInt(settingsObj.defaultDuration) || 120,
        defaultDataLimit: parseInt(settingsObj.defaultDataLimit) || 1024,
        defaultSpeedLimitDown: parseInt(settingsObj.defaultSpeedLimitDown) || 10,
        defaultSpeedLimitUp: parseInt(settingsObj.defaultSpeedLimitUp) || 5,
        maxDevicesPerVoucher: parseInt(settingsObj.maxDevicesPerVoucher) || 3,
      });

      securityForm.reset({
        contentFiltering: settingsObj.contentFiltering === "true",
        requireTermsAcceptance: settingsObj.requireTermsAcceptance === "true",
        sessionLogging: settingsObj.sessionLogging === "true",
        sessionTimeout: parseInt(settingsObj.sessionTimeout) || 30,
      });

      notificationForm.reset({
        emailNotifications: settingsObj.emailNotifications === "true",
        adminEmail: settingsObj.adminEmail || "admin@example.com",
        lowCreditAlerts: settingsObj.lowCreditAlerts === "true",
        alertThreshold: parseInt(settingsObj.alertThreshold) || 50,
      });
    }
  }, [settings, settingsObj, networkForm, voucherForm, securityForm, notificationForm]);

  const updateSettingsMutation = useMutation({
    mutationFn: async (data: { key: string; value: string }) => {
      await apiRequest("PUT", `/api/settings/${data.key}`, { value: data.value });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Settings updated successfully",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/settings"] });
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const saveAllSettings = async () => {
    try {
      const networkData = networkForm.getValues();
      const voucherData = voucherForm.getValues();
      const securityData = securityForm.getValues();
      const notificationData = notificationForm.getValues();

      // Save all settings
      const updates = [
        ...Object.entries(networkData).map(([key, value]) => ({ key, value: String(value) })),
        ...Object.entries(voucherData).map(([key, value]) => ({ key, value: String(value) })),
        ...Object.entries(securityData).map(([key, value]) => ({ key, value: String(value) })),
        ...Object.entries(notificationData).map(([key, value]) => ({ key, value: String(value) })),
      ];

      await Promise.all(updates.map(update => updateSettingsMutation.mutateAsync(update)));
    } catch (error) {
      // Error handled by mutation
    }
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {[...Array(4)].map((_, i) => (
            <Card key={i} className="animate-pulse">
              <CardContent className="p-6">
                <div className="h-96 bg-gray-200 rounded"></div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-lg font-semibold text-gray-900">System Settings</h3>
          <p className="text-sm text-gray-500">Configure your WiFi voucher system preferences</p>
        </div>
        <Button 
          onClick={saveAllSettings}
          disabled={updateSettingsMutation.isPending}
          className="flex items-center space-x-2"
        >
          <Save size={16} />
          <span>{updateSettingsMutation.isPending ? "Saving..." : "Save All"}</span>
        </Button>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Network Settings */}
        <Card>
          <CardHeader className="flex flex-row items-center space-x-2">
            <Network className="text-primary" size={20} />
            <CardTitle>Network Configuration</CardTitle>
          </CardHeader>
          <CardContent>
            <Form {...networkForm}>
              <div className="space-y-4">
                <FormField
                  control={networkForm.control}
                  name="ssid"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>WiFi Network Name (SSID)</FormLabel>
                      <FormControl>
                        <Input {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={networkForm.control}
                  name="gatewayIp"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Gateway IP Address</FormLabel>
                      <FormControl>
                        <Input {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={networkForm.control}
                  name="dnsServers"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>DNS Servers</FormLabel>
                      <FormControl>
                        <Input {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={networkForm.control}
                  name="maxConcurrentUsers"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Maximum Concurrent Users</FormLabel>
                      <FormControl>
                        <Input 
                          type="number"
                          {...field}
                          onChange={(e) => field.onChange(parseInt(e.target.value))}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>
            </Form>
          </CardContent>
        </Card>

        {/* Voucher Defaults */}
        <Card>
          <CardHeader className="flex flex-row items-center space-x-2">
            <Wifi className="text-green-600" size={20} />
            <CardTitle>Default Voucher Settings</CardTitle>
          </CardHeader>
          <CardContent>
            <Form {...voucherForm}>
              <div className="space-y-4">
                <FormField
                  control={voucherForm.control}
                  name="defaultDuration"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Default Duration (minutes)</FormLabel>
                      <Select 
                        onValueChange={(value) => field.onChange(parseInt(value))}
                        value={field.value?.toString()}
                      >
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="120">2 hours</SelectItem>
                          <SelectItem value="240">4 hours</SelectItem>
                          <SelectItem value="1440">24 hours</SelectItem>
                          <SelectItem value="10080">7 days</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={voucherForm.control}
                  name="defaultDataLimit"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Default Data Limit (MB)</FormLabel>
                      <Select 
                        onValueChange={(value) => field.onChange(value === "unlimited" ? undefined : parseInt(value))}
                        value={field.value?.toString() || "unlimited"}
                      >
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="500">500 MB</SelectItem>
                          <SelectItem value="1024">1 GB</SelectItem>
                          <SelectItem value="5120">5 GB</SelectItem>
                          <SelectItem value="unlimited">Unlimited</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="grid grid-cols-2 gap-3">
                  <FormField
                    control={voucherForm.control}
                    name="defaultSpeedLimitDown"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Download (Mbps)</FormLabel>
                        <FormControl>
                          <Input 
                            type="number"
                            {...field}
                            onChange={(e) => field.onChange(parseInt(e.target.value))}
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={voucherForm.control}
                    name="defaultSpeedLimitUp"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Upload (Mbps)</FormLabel>
                        <FormControl>
                          <Input 
                            type="number"
                            {...field}
                            onChange={(e) => field.onChange(parseInt(e.target.value))}
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <FormField
                  control={voucherForm.control}
                  name="maxDevicesPerVoucher"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Max Devices per Voucher</FormLabel>
                      <FormControl>
                        <Input 
                          type="number"
                          {...field}
                          onChange={(e) => field.onChange(parseInt(e.target.value))}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>
            </Form>
          </CardContent>
        </Card>

        {/* Security Settings */}
        <Card>
          <CardHeader className="flex flex-row items-center space-x-2">
            <Shield className="text-yellow-600" size={20} />
            <CardTitle>Security & Access Control</CardTitle>
          </CardHeader>
          <CardContent>
            <Form {...securityForm}>
              <div className="space-y-4">
                <FormField
                  control={securityForm.control}
                  name="contentFiltering"
                  render={({ field }) => (
                    <FormItem className="flex items-center justify-between">
                      <div>
                        <FormLabel>Enable Content Filtering</FormLabel>
                        <p className="text-xs text-gray-500">Block access to inappropriate websites</p>
                      </div>
                      <FormControl>
                        <Switch
                          checked={field.value}
                          onCheckedChange={field.onChange}
                        />
                      </FormControl>
                    </FormItem>
                  )}
                />

                <FormField
                  control={securityForm.control}
                  name="requireTermsAcceptance"
                  render={({ field }) => (
                    <FormItem className="flex items-center justify-between">
                      <div>
                        <FormLabel>Require Terms Acceptance</FormLabel>
                        <p className="text-xs text-gray-500">Users must agree to terms before connecting</p>
                      </div>
                      <FormControl>
                        <Switch
                          checked={field.value}
                          onCheckedChange={field.onChange}
                        />
                      </FormControl>
                    </FormItem>
                  )}
                />

                <FormField
                  control={securityForm.control}
                  name="sessionLogging"
                  render={({ field }) => (
                    <FormItem className="flex items-center justify-between">
                      <div>
                        <FormLabel>Session Logging</FormLabel>
                        <p className="text-xs text-gray-500">Keep detailed logs of user sessions</p>
                      </div>
                      <FormControl>
                        <Switch
                          checked={field.value}
                          onCheckedChange={field.onChange}
                        />
                      </FormControl>
                    </FormItem>
                  )}
                />

                <FormField
                  control={securityForm.control}
                  name="sessionTimeout"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Session Timeout (minutes)</FormLabel>
                      <FormControl>
                        <Input 
                          type="number"
                          {...field}
                          onChange={(e) => field.onChange(parseInt(e.target.value))}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>
            </Form>
          </CardContent>
        </Card>

        {/* Notification Settings */}
        <Card>
          <CardHeader className="flex flex-row items-center space-x-2">
            <Bell className="text-purple-600" size={20} />
            <CardTitle>Notifications & Alerts</CardTitle>
          </CardHeader>
          <CardContent>
            <Form {...notificationForm}>
              <div className="space-y-4">
                <FormField
                  control={notificationForm.control}
                  name="emailNotifications"
                  render={({ field }) => (
                    <FormItem className="flex items-center justify-between">
                      <div>
                        <FormLabel>Email Notifications</FormLabel>
                        <p className="text-xs text-gray-500">Receive system alerts via email</p>
                      </div>
                      <FormControl>
                        <Switch
                          checked={field.value}
                          onCheckedChange={field.onChange}
                        />
                      </FormControl>
                    </FormItem>
                  )}
                />

                <FormField
                  control={notificationForm.control}
                  name="adminEmail"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Admin Email</FormLabel>
                      <FormControl>
                        <Input type="email" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={notificationForm.control}
                  name="lowCreditAlerts"
                  render={({ field }) => (
                    <FormItem className="flex items-center justify-between">
                      <div>
                        <FormLabel>Low Credit Alerts</FormLabel>
                        <p className="text-xs text-gray-500">Alert when vouchers are running low</p>
                      </div>
                      <FormControl>
                        <Switch
                          checked={field.value}
                          onCheckedChange={field.onChange}
                        />
                      </FormControl>
                    </FormItem>
                  )}
                />

                <FormField
                  control={notificationForm.control}
                  name="alertThreshold"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Alert Threshold (vouchers remaining)</FormLabel>
                      <FormControl>
                        <Input 
                          type="number"
                          {...field}
                          onChange={(e) => field.onChange(parseInt(e.target.value))}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>
            </Form>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

